package day0104;

public class book29_01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x; // 변수 x 선언
		x = 10; // 변수 x에 10 값을 저장, 10진수 정수의 저장을 위한 메모리 공간을 할당
				// 그리고 그 메모리공간의 이름은 x라고 하겠다
		int y = 20; // 변수 y를 선언하고 2 값을 저장
		int result = x + y; // 변수 result를 선언하고 변수 x와 y를 더한 값을 저장
		//System.out.println(result);
		System.out.println(result); // 출력문은 간단하게 함, 모든 것은 변수화로(result)
		System.out.println(x+"+"+y+"="+(x+y));
	}

}
